# Übung Klassenarbeit# Peter Schwarz 16.01.2021clearclcmore offprintHeader(mfilename)#### NetzdatenUN = 400NH = 1;PX2 = 25e3;cpX2 = 0.9;PM3mech = 30e3;etaM3 = 0.91;PM3 = PM3mech/etaM3;cpM3 = 0.84;PX4 = 20e3;cpX4 = 0.88;PX5 = 15e3;cpX5 = 0.8;#### Leitungslängel11=50;l12=80;l13=50;l14=60;l2=l3=l4=l5=20;#### netzdefinitionnet5=[l5,PX5,cpX5];net4=[l4,PX4,cpX4];net3=[l3,PM3,cpM3];net2=[l2,PX2,cpX2];[P34,cp34]=Pges(net3,net4);net1=[l11,PX2,cpX2;      l12,P34,cp34;      l13,PX5,cpX5;      l14,0,0];#### SpannungsfalldUpro = UN/100dU1max =  [1.5 * dUpro, 2.0 * dUpro, 1.5 * dUpro];dU2max = 1 * dUpro;dU34max = 1 * dUpro;dU5max = 1 * dUpro;#### Leitungsdefinitionw5=wire(0,NH,5,net5,dU5max);w4=wire(0,NH,5,net4,dU34max);w3=wire(0,NH,4,net3,dU34max);w2=wire(0,NH,5,net2,dU2max);wnext={"w5","w2","w3","w4"};w1=wire(wnext,NH,5,net1,dU1max);#### Leitungs dimw5 = dimWireNYY(w5);printWire(w5)w4 = dimWireNYY(w4);printWire(w4)w3 = dimWireNYY(w3);printWire(w3)w2 = dimWireNYY(w2);printWire(w2)w1 = dimWireNYY(w1);printWire(w1)