# Musterlösung Rinnetz 09clear clc more off## UN=400; w3=sqrt(3.0); NH=1;printLine("Verbraucherdaten");PX3=PX6 =50e3; cpX3=cpX6=0.9;PX4=35e3; cpX4 =0.87;PM5mech = 44e3;  etaM5=0.916;PM5=PM5mech / etaM5 cpM5 = 0.86;l1=80;l21=40; l22=50; l23=70; l24 = 50; l25 = 50;l5=25;printLine("Netzstrucktur");##net5=[l5,PM5,cpM5];net2=[l21,PX3,cpX3;                l22,PX4,cpX4;                l23,PM5,cpM5;                l24,PX6,cpX6;                l25,0,0];  ## Hier schließen wir das Ringnetzt durch zwei nullennet25=[net2;net5];[P1,cp1] = Pges(net25)net1=[l1,P1,cp1];## SpannungsfälledUpro = UN/100;dU1max=1.5*dUpro;dU2max=[2.5*dUpro,2.5*dUpro,2.5*dUpro,2.5*dUpro];dU5max=5*dUpro-dU1max-dU2max(3);## Leitungsdefinitionw5=wire(0,NH,4,net5,dU5max);w2next={0,0,"w5",0};w2=wire(w2next,NH,5,net2,dU2max);w1next={"w2"};w1=wire(w1next,NH,5,net1,dU1max);## Auslegung der Leitungw5=dimWireNYY(w5);w2= dimWireNYY(w2);w1=dimWireNYY(w1);##  AusgabeprintWire(w5);printWire(w2);printWire(w1);