## Spannungsfall eines zweiseitigen gespeisten Netztes## Peter Schwarz 5.12.2020clearclcmore offprintHeader(mfilename)#### NetzdatenUN=404;NH = 1;IX1 = 32;PM1mech = PM3mech = 20e3;PM2mech = 15e3;etaM1 = etaM3 = 0.9;etaM2 = 0.87;cpM1 = cpM3 = 0.85;cpM2 = 0.83; cpX1 = 0.8;PM1 = PM1mech /etaM1PM2 = PM2mech /etaM2PM3 = PM3mech /etaM3PX1 = 400*IX1*sqrt(3)##l11=200;l12 = 100;l13 = 300;l2 = 100;l31 = 50;l32 = 50;l4 = 100;l5 = 50;#### Netzdefinitionnet5=[l5,PX1,cpX1];net4=[l4,PM2,cpM2];net31 = [l31,PM1,cpM1];net32 = [l32,PM3,cpM3];[P34,cp34] = Pges([net31;net4;net32]);net2 = [l2,P34,cp34];net1 =[l11,P34,cp34;                  l12,PX1,cpX1;                  l13,0,0];#### SpannungsfalldUpro = UN/100dU1max = [3*dUpro,4*dUpro];dU2max = 1.5 * dUpro;dU3max = 1.5 * dUpro;dU4max = 1.5 * dUpro;dU5max = 1.5 * dUpro;#### Leitungsdefinitionw5 = wire(0,NH,5,net5,dU5max);w4 = wire(0,NH,4,net4,dU4max);w31 = wire(0,NH,4,net31,dU3max);w32 = wire(0,NH,4,net32,dU3max);w343next = {"w31","w4","w32"};w2 = wire(w343next,NH,4,net2,dU2max);w25next = {"w2",'w5'};w1 = wire(w25next,NH,5,net1,dU1max);####  Auslegung der Leitungw5 = dimWireNYY(w5);w4 = dimWireNYY(w4);w3 = dimWireNYY(w31);w3 = dimWireNYY(w32);w2 = dimWireNYY(w2);w1 = dimWireNYY(w1);#### AusgabeprintWire(w5)printWire(w4)printWire(w31)printWire(w32)printWire(w2)printWire(w1)