## Strahlennetzte 16 Unterricht vom 01.10.2020##clearclcmore off##printHeader(mfilename)#### Definieren von Netzt##Un= 400;## Motor datenPM1mech = PM2mech = PM3mech = 11e3;cpM1 = cpM2 = cpM3 = 0.87;etaM1 =  etaM2 = etaM3 = 0.91;#### NetzdatenPX1 = PX2 = 15e3;cpPX1 = cpPX2 = 0.95;#### Leitungsl√§ngel11 = 40;l12 = 50;l13 = 30;l2 = 50;l3 = 30;l4 = 20;l5 = 30;#### Motoren BerechnungPM1 = PM1mech / etaM1PM2 = PM2mech / etaM2PM3 = PM3mech / etaM3## ## Netzberechnennet3 = [l3,PX1,cpPX1];net4 = [l4,PM1,cpM1];net5 = [l5,PX2,cpPX2];[P45,cpPX45] = Pges([net4;net5]);net2 = [l2, P45,cpPX45];[P2345,cpPX2345] = Pges([net2;net3]);net1 = [l11,PM1,cpM1;                  l12,PM2,cpM2;                  l13,P2345,cpPX2345]; ## ## Spannungsfall Upro = (Un/100) dU1max = 2*Upro dU2max = 1* Upro dU3max = 3 * Upro dU4max =  2* Upro dU5max = 2*Upro ## ## Leitungsdefinition w5 = wire(0,1,5,net5,dU5max); w4 = wire(0,1,4,net4,dU4max); w2next= {"w4","w5"}; w2 = wire(w2next,1,5,net2,dU2max); w3 = wire(0,1,5,net3,dU3max); w1next= {"w2","w3"}; w1 = wire(w1next,1,5,net1,dU1max); ##  ## Leitungen Dimunsionieren w5 = dimWireNYY(w5); w4 = dimWireNYY(w4); w3 = dimWireNYY(w3); w2 = dimWireNYY(w2); w1 = dimWireNYY(w1);#### AusgabeprintWire(w5)printWire(w4)printWire(w3)printWire(w2)printWire(w1)  