## Übung Klassen Arbeit anhand des Arbeitsblattes Strahlennetzt 16## Datum: 03.10.2020#### Standart Defineclearclcmore off#### Header zur AufgabeprintHeader(mfilename)#### Definition NetzUn = 400;PMallmech  = 11e3;etaMall = 0.91;cpPMall = 0.87;PM1 = PM2 = PM3 = PMallmech/etaMall;PM1,PM2,PM3#### Weiter Netzt DatenPX1 = PX2 = 15e3;cpPX1 = cpPX2 = 0.95;#### Definition Leitungslängenl11 = 40;l12 = 50;l13 = 30;l2 = 50;l3 = 30;l4 = 20;l5 = 30;l6 = 70;#### Definition des Netztesnet5= [l5, PX2, cpPX2];net4 = [l4, PM3, cpPMall];[P45, cp45] = Pges([net4 ; net5]);net3 = [l3, PX1, cpPX1];net2 = [l2, P45, cp45];[P2345,cp2345] = Pges([net2; net3]);net6 = [l6, PM1, cpPMall]net1 = [l11, PM1, cpPMall;                  l12, PM2, cpPMall;                  l13, P2345, cp2345];#### SpannungsfalldU1Pro = Un/100;dUmax = dU1Pro * 5;dU1max = 2* dU1ProdU2max = 1*dU1ProdU3max =  dUmax - dU1maxdU4max = dU5max = dUmax - dU1max - dU2max####  Leitungen erfassenw5 = wire(0,1,5,net5,dU5max);w4 = wire(0,1,4,net4, dU4max);w2next = {"w4", "w5"};w2 = wire(w2next,1,5,net2, dU2max);w3 = wire(0,1,5,net3,dU3max);w1next = {"w2","w3"};w1 = wire(w1next,1,5,net1,dU1max);#### Dimensionierungw5 = dimWireNYY(w5);w4 = dimWireNYY(w4);w3 = dimWireNYY(w3);w2 = dimWireNYY(w2);w1 = dimWireNYY(w1);## AUsgabe des Sechsten NetztesdU6max = dUmax - w1.dU(1)w6 = wire(0,1,4,net6,dU6max);w6 = dimWireNYY(w6);#### Ausgabe#printWire(w6)printWire(w5)printWire(w4)printWire(w3)printWire(w2)printWire(w1)