## Übung mit modifizierter Strahlennetz_16_Aufgabe##04.10.2020##clearclcmore offprintHeader(mfilename)#### NennstromprintLine("Betriebsspannung")Un = 400#### Motor DefinitionPM1mech = PM2mech = 11e3;PM3mech = 12e3;etaPM1 = etaPM2 = 0.91;etaPM3 = 0.90;cpPM1 = cpPM2 = 0.87;cpPM3 = 0.85;#### Motor leistungPM1 = PM1mech/etaPM1;PM2 = PM2mech /etaPM2;PM3 = PM3mech / etaPM3;printLine("Motoren Leistung")PM1 PM2 PM3#### Netz DatenPX1 = PX2 = 15e3;cpPX1 = cpPX2 = 0.95;PX3 = 20e3;cpPX3 = 0.96;#### Leitungslängenl11= 20;l12 = 10;l13 = 100;l2 = 20;l3 = 30;l31= 20;l4 = 20;l5 = 30;l6 = 40;#### Netze definierennet6 = [l6, PX3, cpPX3];net31 = [l31, PX1, cpPX1];[PX316, cpPX36] = Pges ([net31;net6]);net5 = [l5, PX2, cpPX2];net4 = [l4, PM3, cpPM3];[PX45, cpPX45] = Pges ([net4;net5]);net3 = [l3, PX316, cpPX36];net2 = [l2, PX45,cpPX45];[P23456,cp23456] = Pges ([net2; net3]);net1 = [l11,PM1, cpPM1;                  l12,PM2,cpPM2                  l13,P23456,cp23456];#### SpannungsverlustdUpro = Un/100;dUmax = 5*dUpro;dU1max = 2* dUpro;dU2max = dU3max = 1* dUpro;dU4max = dU5max = dUmax - dU1max - dU2max;dU31max = dU6max = dUmax - dU1max - dU3max;#### Wire Auslegen## Unterer Strangw6 = wire(0,1,5,net6,dU6max);w31 = wire(0,1,5,net31,dU31max); # Es muss eine Sicherung Angegeben werden Ansonsten wird einer fehler ausgegeben.w3next = {"w31","w6"};w3 = wire(w3next,1,5,net3,dU3max);## Oberer Strangw4 = wire(0,1,4,net4,dU4max);w5 = wire(0,1,5,net5,dU5max);w2next = {"w4","w4"};w2 = wire (w2next,1,5,net2,dU2max);## Zusammen führen der Strängew1next = {"w2","w3"};w1 = wire(w1next,1,5,net1,dU1max);#### Auslegung der Adernw6 = dimWireNYY(w6);w5 = dimWireNYY(w5);w4 = dimWireNYY(w4);w31 = dimWireNYY(w31);w3 = dimWireNYY(w3);w2 = dimWireNYY(w2);w1 = dimWireNYY(w1);##printWire(w6)printWire(w5)printWire(w4)printWire(w31)printWire(w3)printWire(w2)printWire(w1)