## Vorbereitung Anlagen## Peter Schwarzclcclear more offprintHeader(mfilename)## NewtzdatenUn = 400; Upro = Un/100; NH=1;  LS=2; dUmax = 5*Upro;l1 = 40; l2= 80;l3 = 80; l4=60; l5 = 20;l6 = l7= l8 =  l9 = 20;l10 = 10; l26 = 30;  l27= 20;l28 = 50; l29 = 30;P6mech = 4e3; etaM6 = 0.90; cosM6 = 0.89;P7mech = 15e3;  etaM7 = 0.933;  cosM7 = 0.9;P9mech = P10mech = 11e3;  etaM9  = etaM10 = 0.926;  cosM9 = cosM10 = 0.9;## Fragen ob die Faktor übername bei einem Frequenz umrichter richtig istcosTFU = 0.95;PX8 = 4.5e3; cosX8 = 0.89;PX27 = 10e3; cosX27 = 0.92;PX28 = 1.2e3; cosX28 = 0.82;PX29 = 4.5e3; cosX29 = 0.89;## Elektrische Leistung MotorenPM6 = P6mech / etaM6;PM7 = P7mech / etaM7;PM9 = P9mech / etaM7;PM10 = P10mech / etaM10;## Netze Definierennet6 = [l6,PM6,cosM6];net7 = [l7,PM7,cosTFU];net8 = [l8,PX8,cosX8];net9 = [l9,PM9,cosTFU];net10 = [l10, PM10, cosTFU];net26 = [l26, PM10, cosTFU];net27 = [l27, PX27, cosX27];net28 = [l28, PX28, cosX28];net29 = [l29, PX29, cosX29];[P6789, cp6789] = Pges([net6;net7;net8;net9]);[P1027, cp1027] = Pges([net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net10;net26;net27]);[P2829, cp2829] = Pges([net28;net29]);net3 = [l3, P6789, cp6789];net4 = [l4, P1027, cp1027];net5 = [l5, P2829, cp2829];[P345, cp345] = Pges([net3;net4;net5]);# Beides Speist einnet11 = [l1,P345, cp345;                  l2, 0, 0];# Nur BHKW Speist einnet12 = [l2, P345, cp345];# Nur Skq1 speist einnet13 = [l1, P345, cp345];## SpannungsfälledU11max = 0.8*Upro;dU12max = 1*Upro;dU13max = 0.8*Upro;dU3max = (dUmax - min([dU11max, dU12max,dU13max])) *(1.3/3);dU4max =  (dUmax - min([dU11max, dU12max,dU13max])) *(1/3);dU5max =  (dUmax - min([dU11max, dU12max,dU13max]))  *(1/3);dUV1max = dUmax - dU3max;dUV2max = dUmax - dU4max;dUV3max = dUmax - dU5max;## Leitungen Definierenw6 = wire(0,NH,4, net6,dUV1max);w7 = wire(0,NH,4, net7,dUV1max);w8 = wire(0,NH,5, net8,dUV1max);w9 = wire(0,NH,4, net9,dUV1max);wUV1 = {"w6","w7","w8","w9"};w3 = wire(wUV1, NH, 5, net3,dU3max,0);w10 = wire(0, NH,4, net10, dUV2max);w26 = wire(0, NH, 4, net26, dUV2max);w27 = wire(0, NH, 5, net27, dUV2max);wUV2 = {"w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w10","w26","w27"};w4 = wire(wUV2, NH, 5, net4, dU4max,0);w28 = wire(0, NH, 3, net28, dUV3max);w29 = wire(0, NH, 5, net29, dUV3max);wUV3 = {"w28","w29"};w5 = wire(wUV3, NH, 5, net5, dU5max,0);wHV = {"w3","w4","w5"};## Fall 1w11 = wire(wHV, NH, 5, net11, dU11max,0);## Fall 2w12 = wire(wHV, NH, 5, net12, dU12max,0);# Fall 3w13 = wire(wHV, NH, 5, net13, dU13max,0);## Dimensionierung der Leitungenw6 = dimWireNYY(w6);w7 = dimWireNYY(w7);w8 = dimWireNYY(w8);w9 = dimWireNYY(w9);w3 = dimWireNYY(w3);w10 = dimWireNYY(w10);w26 = dimWireNYY(w26);w27 = dimWireNYY(w27);w4 = dimWireNYY(w4);w28 = dimWireNYY(w28);w29 = dimWireNYY(w29);w5 = dimWireNYY(w5);w11 = dimWireNYY(w11);w12 = dimWireNYY(w12);w13 = dimWireNYY(w13);## AusgabeprintWire(w6);printWire(w7);printWire(w8);printWire(w9);printWire(w3);printWire(w10);printWire(w26);printWire(w27);printWire(w4);printWire(w28);printWire(w29);printWire(w5);printWire(w11);printWire(w12);printWire(w13);## IkmaxZl1 = ZsNYYc(w11.A,1) * (l1/1000)Zl2 = ZsNYYc(w11.A,1) * (l2/1000)Zl3 = ZsNYYc(w3.A) * (l3/1000)Zl4 = ZsNYYc(w4.A) * (l4/1000)Zl5 = ZsNYYc(w5.A) * (l5/1000)Zl6 = ZsNYYc(w6.A) * (l6/1000)ZM6 = ZASMs(P6mech, cosM6 ,etaM6 )ZQ1 = ZQmax(420e6)Zt1 = ZT(630e3,0.04)ZG = ZGk1(500e3)Z1 = Zl2 + ZGZ2 = Zl1 + Zt1 + ZQ1Z3 = Zl3 + Zl6 + ZM6function ret= Iteiler(I2,R1,R2)  ret = I2 * R1/(R1+R2);endfunctionprintLine("Ikmax von LS28")Cmax = 1.1ZLS28 = Zl5 + par(Z1,par(Z2,Z3))IkmaxLS28 = Cmax* Un / (sqrt(3.0) *ZLS28)printpol(IkmaxLS28,"A")printLine("Ikmax von F4")ZF4ges = par(Z2,Z1)IkmaxF4 = Cmax* Un / (sqrt(3.0) *ZF4ges)printpol(IkmaxF4,"A")IkmaxBHKW = Iteiler(IkmaxF4, Z2,Z1);printpol(IkmaxBHKW,"A");IkmaxSQ1=IkmaxF4 - IkmaxBHKW;printpol(IkmaxSQ1,"A");printLine("Ikmin Berechnung");ZQ1min = ZQmin(420e6)ZG1min = ZGk2(500e3)ZG10 = ZGk0(500e3)ZT10 = ZT0(630e3,0.04)Zl1 = ZsNYYw(w11.A,1) * (l1/1000)Zl10 = ZsNYYw0(w11.A,1) * (l1/1000)Zl2 = ZsNYYw(w11.A,1) * (l2/1000)Zl20 = ZsNYYw0(w11.A,1) * (l2/1000)Cmin = 0.95;printLine("Ikmin ZQ1");Zges = ZQ1min + Zt1 + Zl1Z0ges=ZT10 + Zl10IkminQ1 = sqrt(3.0) * Un * Cmin /(2*Zges + Z0ges)printpol(IkminQ1,"A");printLine("Ikmin ZG1");Zgeg = ZG1min  + Zl2Zmit = ZG  + Zl2Z0ges= Zl20 + ZG10IkminG1 = sqrt(3.0) * Un * Cmin /(Zgeg+Zmit + Z0ges)printpol(IkminG1,"A");